                    <div>
                        <h1>{oneProduct.name}</h1>
                        <h1>{oneProduct.price}</h1>
                        <h1>{oneProduct.description}</h1>
                        <h1>{oneProduct.used===true?"Used":"New"}</h1>
                        <button onClick={() => history.goBack()}>Go back</button>
