import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom"
import axios from "axios"
import Delete from "./../components/Delete"

const Displayall = (props) => {

    const [crew, setCrew] = useState(null)

    useEffect(() => {
        axios.get('http://localhost:8000/server/pirates')
            .then(res => {
                console.log(res.data)
                setCrew(res.data)
                console.log(crew)
            }).catch(err => {
                console.log(err)
                setCrew("")
            })
    }, [])

    return (
        <div>
            {
                crew ? (<div>
                    {
                        crew.map((pirate, i) => (
                            <div key={i} style={{display:"flex"}}>
                                <div style={{flex:1}}>{pirate.image}</div>

                                <div style={{flex:3, height:"300px", border:"solid red"}}>
                                    <h1>{pirate.name}</h1>
                                    <div style={{ display: "flex" }}>
                                        <div><Link to={`/pirates/${pirate._id}/home`}>View pirate</Link></div>
                                        <div><Delete id={pirate._id} /></div>
                                    </div>
                                </div>
                            </div>
                        ))
                    }
                </div>
                ) : (
                    <h1>Loading</h1>
                )
            }
        </div>
    )
};

export default Displayall;
