import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from "react-router-dom"
import axios from "axios"
import Delete from "./../components/Delete"

const Individual = () => {
    const [onePirate, setonePirate] = useState(null)
    const { id } = useParams()
    const history = useHistory()
    
    useEffect(() => {
        axios.get(`http://localhost:8000/server/pirates/${id}`)
            .then(res => setonePirate(res.data))
            .catch(err => console.log(err))
    }, [])

    const reload=()=>{
        history.push("/")
    }

    return (
        <div>
            {
                onePirate ?
                    <div>
                        <p>{onePirate.name}</p>
                        <p>{onePirate.price}</p>
                        <p>{onePirate.description}</p>
                        <p>{onePirate.pegleg===true?"Pegleg":""}</p>
                        <p>{onePirate.eyepatch===true?"Eyepatch":""}</p>
                        <p>{onePirate.hookhand===true?"Hookhand":""}</p>
                        <button onClick={() => history.goBack()}>Go back</button>
                        <Delete id={onePirate._id} reload={reload}/>
                    </div>:
                    <p>Loading</p>
            }
        </div>
    )
};

export default Individual;
