import {Link } from "react-router-dom"
import axios from "axios"
import Delete from "./../components/Delete"

const Displayall = (props) => {

    const [crew,setCrew]=useState(null)

    useEffect(() => {
        axios.get('http://localhost:8000/server/pirates')
            .then(res => {
                console.log(res.data)
                setCrew(res.data)
                console.log(crew)
            }).catch(err => {
                console.log(err)
                setCrew("")
            })
    }, [])

    return (
        <div>
            {
                crew ? (
                    <div style={{display:"flex"}}>
                        <div style={{flex:1}}>
                        image
                        </div>
                        
                        {
                                crew.map((pirate,i)=>(
                                   <div  style={{flex:5}} key={i}>
                                       <p>{pirate.name}</p>
                                       <div>
                                        <div>awdawd</div>
                                        <div>awdawd</div>
                                        </div>
                                   </div>
                                   
                                ))
                            }
                        


                    </div>
                ) : (
                    <h1>Loading</h1>
                )

            }
        </div>
    )
};

export default Displayall;
